import { Component, OnInit } from "@angular/core";
import { UserService } from "../services/user.service";

@Component({
  selector: "app-home",
  templateUrl: "./home.component.html",
  styleUrls: ["./home.component.scss"],
})
export class HomeComponent implements OnInit {
  counts: any;

  constructor(private userService: UserService) {}

  async ngOnInit() {
    try {
      this.counts = await this.userService.getCounts();
      console.log(this.counts);
    } catch (error) {
      console.error("Error fetching counts in HomeComponent:", error);
    }
  }
}
<!-- home.component.html -->
<div class="container mt-4">
    <div class="row">
        <div class="col-lg-3 col-md-6 mb-4">
            <div class="card text-white bg-primary shadow">
                <div class="card-body">
                    <h5 class="card-title">Total Users</h5>
                    <p class="card-text display-4">{{ counts.totalUsers }}</p>
                </div>
            </div>
        </div>
        <div class="col-lg-3 col-md-6 mb-4">
            <div class="card text-white bg-success shadow">
                <div class="card-body">
                    <h5 class="card-title">Active Users</h5>
                    <p class="card-text display-4">
                        {{ counts.totalActiveUsers }}
                    </p>
                </div>
            </div>
        </div>
        <div class="col-lg-3 col-md-6 mb-4">
            <div class="card text-white bg-danger shadow">
                <div class="card-body">
                    <h5 class="card-title">Not Active Users</h5>
                    <p class="card-text display-4">
                        {{ counts.totalNotActiveUsers }}
                    </p>
                </div>
            </div>
        </div>
        <div class="col-lg-3 col-md-6 mb-4">
            <div class="card text-white bg-warning shadow">
                <div class="card-body">
                    <h5 class="card-title">Today Joined Users</h5>
                    <p class="card-text display-4">
                        {{ counts.totalUserjoinedToday }}
                    </p>
                </div>
            </div>
        </div>
    </div>
</div>
