import { Component, OnInit } from "@angular/core";
import { UserService } from "../../services/user.service";

@Component({
  selector: "app-user-table",
  templateUrl: "./user-table.component.html",
  styleUrls: ["./user-table.component.scss"],
})
export class UserTableComponent implements OnInit {
  users: any = [];
  totalUsers: number = 0;
  totalPages: number = 1;
  currentPage: number = 1;
  pageSize: number = 10;
  searchQuery: string = "";

  constructor(private userService: UserService) {}

  ngOnInit(): void {
    this.fetchUsers();
  }

  fetchUsers(): void {
    this.userService
      .getUsers(this.currentPage, this.pageSize, this.searchQuery)
      .subscribe({
        next: (response) => {
          this.users = response.users;
          this.totalUsers = response.total;
          this.totalPages = response.totalPages;
        },
        error: (error) => {
          console.error("Error fetching users", error);
        },
      });
  }

  pageChanged(event: any): void {
    this.currentPage = event.page;
    this.fetchUsers();
  }
}
<div class="container mt-3">
    <h2>User List</h2>

    <div class="mb-3">
        <input
            type="text"
            class="form-control"
            placeholder="Search users..."
            [(ngModel)]="searchQuery"
            (keyup.enter)="fetchUsers()"
        />
    </div>

    <div class="table-responsive">
        <table class="table table-hover">
            <thead class="table-dark">
                <tr>
                    <th scope="col">#</th>
                    <th scope="col">Username</th>
                    <th scope="col">Name</th>
                    <th scope="col">Email</th>
                    <th scope="col">Mobile</th>
                    <th scope="col">UPI Number</th>
                    <th scope="col">Status</th>
                    <th scope="col">Joined</th>
                    <th scope="col">Updated</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let user of users; let i = index">
                    <th scope="row">
                        {{ (currentPage - 1) * pageSize + i + 1 }}
                    </th>
                    <td>{{ user.username }}</td>
                    <td>{{ user.name }}</td>
                    <td>{{ user.email }}</td>
                    <td>{{ user.mobile_number }}</td>
                    <td>{{ user.upi_number }}</td>
                    <td>{{ user.status }}</td>
                    <td>{{ user.createdAt | date: 'short' }}</td>
                    <td>{{ user.updatedAt | date: 'short' }}</td>
                </tr>
            </tbody>
        </table>
    </div>

    <div class="d-flex justify-content-between mt-3">
        <p>Showing {{ users.length }} of {{ totalUsers }} users</p>
        <ngb-pagination
            [collectionSize]="totalUsers"
            [(page)]="users.currentpage"
            [maxSize]="5"
            [boundaryLinks]="true"
            [rotate]="true"
            (pageChange)="pageChanged($event)"
        ></ngb-pagination>
    </div>
</div>
