import { Component, OnInit } from "@angular/core";
import { UserService } from "../services/user.service";

@Component({
  selector: "app-home",
  templateUrl: "./home.component.html",
  styleUrls: ["./home.component.scss"],
})
export class HomeComponent implements OnInit {
  counts: any;

  constructor(private userService: UserService) {}

  async ngOnInit() {
    try {
      this.counts = await this.userService.getCounts();
      console.log(this.counts);
    } catch (error) {
      console.error("Error fetching counts in HomeComponent:", error);
    }
  }
}
<!-- home.component.html -->
<div class="card-container">
    <div class="card total-users">
        <h2>Total Users</h2>
        <p>{{ counts.totalUsers }}</p>
    </div>
    <div class="card active-users">
        <h2>Active Users</h2>
        <p>{{ counts.totalActiveUsers }}</p>
    </div>
    <div class="card not-active-users">
        <h2>Not Active Users</h2>
        <p>{{ counts.totalNotActiveUsers }}</p>
    </div>
</div>
